responder.transformResultsScript:
var getProductsData = botContext.getBotVariable("api_getProducts");
var getProductsJson = JSON.parse(getProductsData.jsonData);
var getProductsData = getProductsJson.api_getProducts;
var products = getProductsData.products;
botContext.setBotVariable("products", products, true, false);

conversationMessage.preProcessMessage:
var productList = botContext.getBotVariable("products"); // get the products from the context service
var storeURL = botContext.getBotVariable("storeURL");


var tileArray = [];
tileArray.length = 0;
var addedItems = [];
// loop over all products
for (var i = 0; i < productList.length; i++) {
  botContext.printDebugMessage("item # " + i);
  botContext.printDebugMessage(productList[i].title);
  // check if the item has a variant with the desired size and design
  for (var j = 0; j < productList[i].variants.length; j++) {
    if (productList[i].variants[j].inventory_quantity > 0 && // the variant is in stock
        addedItems.indexOf(productList[i].id) == -1 && // the product hasn't already been added to the carousel
        tileArray.length < 10 // the carousel isn't already full
       ) {
 // check for image and set it or set a placeholder
      var placeholder = "https://cdn.shopify.com/s/files/1/0549/9620/5611/products/image1_ce246dcf-9b79-47cd-84d1-18a96ff6ef18.jpg?v=1639072935";
      var imageSrc = "https://cdn.shopify.com/s/files/1/0549/9620/5611/products/image1_ce246dcf-9b79-47cd-84d1-18a96ff6ef18.jpg?v=1639072935";
      if (!productList[i].image) {
        imageSrc = placeholder;
        botContext.printDebugMessage("the product has no image - using placeholder");
      } else if (!productList[i].variants[j].image_id) {
         log("no image associated with this variant");
         imageSrc = placeholder; 
        } else if (productList[i].variants[j].image_id) {
          var imageId = productList[i].variants[j].image_id;
          // loop through images to find a match with the variant which is also long
          for (var q = 0; q < productList[i].images.length; q++) {
          if (productList[i].images[q].id == imageId && productList[i].images[q].height > productList[i].images[q].width) {
            log(q);
            imageSrc = productList[i].images[q].src;
            botContext.printDebugMessage("the product has an image");
          }
        }       
      } else {
        imageSrc = placeholder;
      }
      // create the tile
  var tile = {
    type: "vertical",
    elements: []
  };
  // add the image to tile
      log(imageSrc);
  tile.elements.push({
        type: "image",
        url: imageSrc, // this will change once images are added (productList[i].images[0].src)
        click: {
          actions: [
            {
              type: "link",
              uri: storeURL + "/products/" + productList[i].handle + "?variant=" + productList[i].variants[j].id,
              target: "blank"
            }
          ]
        }
      });
  // add the title to tile
  tile.elements.push({
        type: "text",
        style: {
          bold: true,
          size: "large"
        },
        text: productList[i].title + " - " + productList[i].variants[j].option2,
        tooltip: productList[i].title + " - " + productList[i].variants[j].option2
      });
      // add a link to the product to the tile
      tile.elements.push({
        title: "See details",
        type: "button",
        click: {
          actions: [
            {
              type: "link",
              uri: storeURL + "/products/" + productList[i].handle + "?variant=" + productList[i].variants[j].id,
              target: "blank"
            }
          ]
        },
        tooltip: "See detailed info about " + productList[i].title
      });
      // Add an 'add to cart' button which will store the variant ID to later form a dyanamic cart link
      tile.elements.push({
        title: "Add to cart",
        type: "button",
        click: {
          actions: [
            {
              type: "publishText",
              text: "Product #: " + productList[i].variants[j].id
            }
          ]
        },
        tooltip: "Add " + productList[i].title + " to your cart"
      });
  tileArray.push(tile);
  addedItems.push(productList[i].id);
    }
  }
}

var carousel = {
  "padding": 10,
  "type": "carousel",
  "elements": tileArray
};
// handle single length array results
if (tileArray.length == 1) {
  var carouselJson = JSON.stringify(tileArray[0]);
} else {
  var carouselJson = JSON.stringify(carousel);
}
log(carouselJson);
botContext.setBotVariable("carousel", carouselJson, true, false);
botContext.printDebugMessage(tileArray.length);
botContext.printDebugMessage(addedItems);

botContext.printDebugMessage(botContext.getCurrentUserMessage());
var text = botContext.getCurrentUserMessage();
var variantId = text.split(' ')[2];
if (botContext.getBotVariable("activeCart") == "false") { // if the cartURL variable doesn't exist already, create it
  botContext.printDebugMessage("this is the user's first time adding to the cart");
  var cartURL = "https://bella-loves-me.myshopify.com/cart/" + variantId;
  botContext.setBotVariable("cartURL", cartURL, true, false);
  botContext.setBotVariable("activeCart", "true", true, false);
  botContext.printDebugMessage(variantId);
  botContext.printDebugMessage(cartURL);
} else { // if it already exists, append it
  botContext.printDebugMessage("this is NOT the user's first itme adding to the cart");
  var cartURL = botContext.getBotVariable("cartURL") + "," + variantId;
  botContext.setBotVariable("cartURL", cartURL, true, false);
  botContext.printDebugMessage(variantId);
  botContext.printDebugMessage(cartURL);
}

conversationMessage.processUserResponse:
function isInt(value) {
  var x = parseFloat(value);
  return !isNaN(value) && (x | 0) === x;
}
var input = botContext.getCurrentUserMessage();
botContext.printDebugMessage(isInt(input));
if (isInt(input)) {
  var newCartURL = botContext.getBotVariable("cartURL") + ":" + input;
  botContext.setBotVariable("cartURL", newCartURL, true, false);
}

conversationMessage.preProcessMessage:
var activeCart = botContext.getBotVariable("activeCart");
var cartMessage = "";
if (activeCart == "true") {
  cartMessage = "Let me fetch your cart information. One moment.";
  botContext.sendMessage(cartMessage);
  botContext.setTriggerNextMessage('view cart');
} else {
 cartMessage = "Sorry. I could not find any items in your shopping cart.";
  botContext.sendMessage(cartMessage);
  botContext.setTriggerNextMessage('9.2 Anything else');
}

